import React from 'react';
import { useLocation } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const location = useLocation();

  return (
    <div className="sidebar">
      {/* Conditionally display the image only on the home page */}
      {location.pathname === '/' && (
        <div className="image-container">
          <img src={`${process.env.PUBLIC_URL}/linux-terminal.png`} alt="Linux Terminal" className="terminal-image" />
        </div>
      )}
      <nav>
        <ul>
          <li><a href="/">Home</a></li>
          <li><a href="/whoami">Who Am I</a></li>
          <li><a href="/articles">Articles</a></li>
        </ul>
      </nav>
    </div>
  );
};

export default Sidebar;
